(3, 'BRZuJEe', 'Object new Date milliseconds', '<h1>Object new Date(milliseconds)</h1>\r\n\r\n<h4>Zero Time : <span id="now"></span></h4>\r\n\r\n<h3 id="time"></h3>\r\n<button onclick="play1()">new Date(86400000)</button>\r\n<button onclick="play2()">new Date(100000000000)</button>\r\n<button onclick="play3()">new Date(-100000000000)</button>\r\n\r\n<p class="explain">\r\n  Sehari (24 jam) adalah 86 400 000 milliseconds\r\n</p>', '#time {\r\n  border: 1px solid red;\r\n  text-align: center;\r\n}', 'document.getElementById(''now'').innerHTML = new Date(0);\r\nfunction play1(){\r\n  var time = new Date(86400000);\r\n  document.getElementById(''time'').innerHTML = time; \r\n}\r\nfunction play2(){\r\n  var time = new Date(100000000000);\r\n  document.getElementById(''time'').innerHTML = time; \r\n}\r\nfunction play3(){\r\n  var time = new Date(-100000000000);\r\n  document.getElementById(''time'').innerHTML = time; \r\n}'),
(1, 'BRZuJEe', 'Format waktu JavaScript', '<h1>Format standard waktu JavaScript</h1>\r\n\r\n<h3 id="time"></h3>', '', 'let times = Date();\r\ndocument.getElementById(''time'').innerHTML = times;'),
(2, 'BRZuJEe', 'Object new Date', '<h1>Object new Date</h1>\r\n  \r\n<h3 id="time"></h3>', '', 'var times = new Date();\r\ndocument.getElementById(''time'').innerHTML = times;'),
(4, 'BRZuJEe', 'Object new Date numbers', '<h1>Object new Date(Numbers)</h1>\r\n  \r\n<h3>Kapan peristiwa proklamasi kemerdekaan Indonesia ?</h3>\r\n<button onclick="play1()">7 argument</button>\r\n<button onclick="play2()">6 argument</button>\r\n<button onclick="play3()">5 argument</button>\r\n<button onclick="play4()">4 argument</button>\r\n<button onclick="play5()">3 argument</button>\r\n<button onclick="play6()">2 argument</button>\r\n\r\n<p class="explain">\r\n  Argument yang mewakili tahun dan bulan harus ditulis,\r\n  karena jika hanya menuliskan salah satunya saja\r\n  maka akan dianggap sebagai milliseconds.\r\n  JavaScript menghitung nama bulan mulai dari angka 0,\r\n  sehingga Januari = 0 sedangkan Desember = 11\r\n</p>', '', 'function play1(){\r\n  alert( new Date(1945,07,17,10,45,30,20) );\r\n}\r\nfunction play2(){\r\n  alert( new Date(1945,07,17,10,45,30) );\r\n}\r\nfunction play3(){\r\n  alert( new Date(1945,07,17,10,45) );\r\n}\r\nfunction play4(){\r\n  alert( new Date(1945,07,17,10) );\r\n}\r\nfunction play5(){\r\n  alert( new Date(1945,07,17) );\r\n}\r\nfunction play6(){\r\n  alert( new Date(1945,07) );\r\n}'),
(5, 'BRZuJEe', 'Object new Date string', '<h1>Object new Date(String)</h1>\r\n\r\n<h3>Kapan peristiwa proklamasi kemerdekaan Indonesia ?</h3>\r\n<button onclick="play1()">ISO Standard</button>\r\n<button onclick="play2()">Short Date</button>\r\n<button onclick="play3()">Long Date</button>', '', 'function play1(){\r\n  alert( new Date("1945-08-17") );\r\n}\r\nfunction play2(){\r\n  alert( new Date("08/17/1945") );\r\n  // urutan DD dengan MM yang terbalik akan invalid\r\n  alert( new Date("17/08/1945") );\r\n}\r\nfunction play3(){\r\n  alert( new Date("Aug 17 1945") );\r\n  alert( new Date("17 Aug 1945") );\r\n  // menuliskan nama bulan secara utuh dalam bahasa inggris\r\n  alert( new Date("17 August 1945") );\r\n}'),
(6, 'BRZuJEe', 'Method Get Dates', '<h1>Method Get Dates</h1>\r\n\r\n<h3 id="now"></h3>\r\n<button onclick="play1()">tahun</button>\r\n<button onclick="play2()">bulan</button>\r\n<button onclick="play3()">tanggal</button>\r\n<button onclick="play4()">hari</button>\r\n<button onclick="play5()">jam</button>\r\n<button onclick="play6()">menit</button>\r\n<button onclick="play7()">detik</button>\r\n<button onclick="play8()">milidetik</button>\r\n<button onclick="play9()">time milidetik</button>\r\n\r\n<p class="explain">\r\n  untuk output method <code>getMonth()</code> dan <code>getDay()</code> akan dimulai penghitungan mulai dari angka 0.\r\n  getMonth akan menghasilkan nilai 0 - 11 (12 bulan), 0 untuk January dan 11 untuk December.\r\n  getDay akan menghasilkan nilai 0 - 6 (7 hari), 0 untuk Sunday dan 6 untuk Saturday \r\n</p>', '', 'var now = new Date();\r\ndocument.getElementById(''now'').innerHTML = now;\r\nfunction play1(){\r\n  alert( now.getFullYear() );\r\n}\r\nfunction play2(){\r\n  alert( now.getMonth() );\r\n}\r\nfunction play3(){\r\n  alert( now.getDate() );\r\n}\r\nfunction play4(){\r\n  alert( now.getDay() );\r\n}\r\n  function play5(){\r\n  alert( now.getHours() );\r\n}\r\nfunction play6(){\r\n  alert( now.getMinutes() );\r\n}\r\nfunction play7(){\r\n  alert( now.getSeconds() );\r\n}\r\nfunction play8(){\r\n  alert( now.getMilliseconds() );\r\n}\r\nfunction play9(){\r\n  alert( now.getTime() );\r\n}'),
(7, 'BPI6T7Y', 'This pada Scope Global', '<h1>This pada Scope Global</h1>\r\n\r\n<h3>this nilainya adalah <span id="hasil"></span></h3>\r\n<button onclick="play()">klik saya</button>\r\n\r\n<p class="explain">Buka DevTools Console (F12) untuk melihat object</p>', '', 'var glob = this;\r\nfunction play(){\r\n  document.getElementById(''hasil'').innerHTML = glob;\r\n  console.log(glob);\r\n}'),
(8, 'BPI6T7Y', 'This pada Function', '<h1>This pada Function</h1>\r\n\r\n<h3>this nilainya adalah <span id="hasil"></span></h3>\r\n<button onclick="play()">klik saya</button>\r\n\r\n<p class="explain">Buka DevTools Console (F12) untuk melihat object</p>', '', 'function play(){\r\n  var func = this;\r\n  document.getElementById(''hasil'').innerHTML = func;\r\n  console.log(func);\r\n}'),
(9, 'BPI6T7Y', 'This pada Method', '<h1>This pada Method</h1>\r\n\r\n<h3>Jalankan method lengkap() dari object nama</h3>\r\n<h3>this nilainya adalah <span id="hasil"></span></h3>\r\n<button onclick="nama.lengkap()">klik saya</button>\r\n\r\n<p class="explain">Buka DevTools Console (F12) untuk melihat object</p>', '', 'var namaLengkap;\r\nvar nama = {\r\n  depan : ''John'',\r\n  belakang : ''Simon'',\r\n  lengkap : function(){\r\n    namaLengkap = this.depan+" "+this.belakang;\r\n    alert(namaLengkap);\r\n    document.getElementById(''hasil'').innerHTML = this;\r\n    console.log(this);\r\n  }\r\n}'),
(10, 'BPI6T7Y', 'This pada Event Handler', '<h1>This pada Event Handler</h1>\r\n\r\n<button onclick="play(this)">element button</button>\r\n<input onclick="play(this)" placeholder="element input">\r\n<span onclick="play(this)">element span</span>\r\n\r\n<h3>this nilainya adalah <span id="hasil"></span></h3>', '', 'function play(arg) {\r\n  document.getElementById(''hasil'').innerHTML = arg;\r\n  console.log(arg);\r\n}'),
(11, 'BHztSL0', 'Membuat Object Literal', '<h1>Object Literal</h1>\r\n\r\n<h3>Buka DevTool untuk memastikan object berhasil dibuat</h3>', '', 'var orang = {\r\n  nama : ''John'',\r\n  umur : 28,\r\n  profesi : ''programmer'',\r\n  intro : function(){\r\n    alert(`Saya ${this.nama} bekerja sebagai ${this.profesi}`);\r\n  }\r\n};\r\nconsole.log(orang);'),
(12, 'BHztSL0', 'Membuat Object Function', '<h1>Object Function</h1>\r\n\r\n<h3>Buka DevTool untuk memastikan object berhasil dibuat</h3>', '', 'function buatObject(nama,umur,profesi){\r\n  var orang = {};\r\n  orang.nama = nama;\r\n  orang.umur = umur;\r\n  orang.profesi = profesi;\r\n  return orang;\r\n}\r\nvar orang1 = buatObject(''John'',38,''Guru'');\r\nvar orang2 = buatObject(''Simon'',29,''Manager'');\r\nconsole.log(orang1);\r\nconsole.log(orang2);'),
(13, 'BHztSL0', 'Membuat Object Constructor', '<h1>Object Constructor</h1>\r\n\r\n<h3>Buka DevTools untuk memastikan object berhasil dibuat</h3>', '', 'function BuatObjectBaru(nama,umur,profesi){\r\n  this.nama = nama;\r\n  this.umur = umur;\r\n  this.profesi = profesi;\r\n  this.intro = function(){\r\n    alert(`Saya ${this.nama} bekerja sebagai ${this.profesi}`);\r\n  }\r\n}\r\n\r\nvar orang1 = new BuatObjectBaru(''John'',37,''Pengusaha'');\r\nconsole.log(orang1);'),
(14, 'BHztSL0', 'Mengakses Object Property', '<h1>Mengakses Object Property</h1>\r\n\r\n<button onclick="play1()">orang.nama</button>\r\n<button onclick="play2()">orang.umur</button>\r\n<button onclick="play3()">orang[''profesi'']</button>', '', 'var orang = { nama: ''John'', profesi : ''programmer'', umur : 28 };\r\nfunction play1(){\r\n  alert(`Nama saya adalah ${orang.nama}`);\r\n}\r\nfunction play2() {\r\n  alert(`Umur saya sekarang ${orang.umur} tahun`);\r\n}\r\nfunction play3() {\r\n  alert(`Saya bekerja sebagai ${orang[''profesi'']}`);\r\n}'),
(15, 'BHztSL0', 'Mengakses Object Method', '<h1>Mengakses Object Method</h1>\r\n\r\n<button onclick="play()">orang.sapa()</button>', '', 'var orang = { \r\n  nama: ''John'', \r\n  profesi : ''programmer'', \r\n  umur : 28, \r\n  sapa : function(){\r\n    alert(''Hai, saya ''+this.nama+'', bekerja sebagai ''+this.profesi+'''');\r\n  }\r\n};\r\nfunction play(){\r\n  orang.sapa();\r\n}'),
(16, 'BbZNoYE', 'Manipulasi Element HTML', '<h1>Interaksi Element HTML</h1>\r\n\r\n<img id="lampu" src="http://localhost/helloworld/assets/img/lamp_off.gif">\r\n<button id="btn">klik untuk nyalakan lampu</button>', 'button {\r\n  margin: 10px auto;\r\n  display: block;\r\n  min-width: 300px;\r\n}\r\nimg { \r\n  margin: 30px auto;\r\n  display: block;\r\n}', 'var on, off, tombol, lampu;\r\non  = "http://localhost/helloworld/assets/img/lamp_on.gif";\r\noff = "http://localhost/helloworld/assets/img/lamp_off.gif"; \r\ntombol = document.getElementById(''btn'');\r\nlampu = document.getElementById(''lampu'');\r\n\r\ntombol.onclick = function(){\r\n  tombol.classList.toggle(''menyala'');\r\n  if(tombol.classList.contains(''menyala'')){\r\n    lampu.src = on;\r\n    tombol.innerHTML = ''klik lagi untuk padamkan lampu'';\r\n  } else {\r\n    lampu.src = off;\r\n    tombol.innerHTML = ''klik untuk nyalakan lampu'';      \r\n  }\r\n}'),
(17, 'Bq0ug2s', 'Statement pada JavaScript', '<h1>Statement JavaScript</h1>', '', '// statement membuatkan/ menyediakan nama variable\r\nvar str;\r\n// statement mengisi variable dengan nilai berupa string\r\nstr = ''saya sedang belajar'';\r\n// statement menampilkan isi variable ke dalam console\r\nconsole.log(str);'),
(18, 'Bq0ug2s', 'JavaScript Comment', '<h1>JavaScript Comment</h1>\r\n\r\n<button id="btn">klik saya</button>\r\n\r\n<p class="explain">Agar tombol di atas dapat berfungsi, hapus tanda komentar</p>', '', 'var tombol;\r\ntombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  // alert(''saya tidak akan tampil sebelum tanda komentar dihapus'');\r\n};'),
(19, 'BDMJggi', 'JavaScript Inline', '<h1>JavaScript Inline</h1>\r\n\r\n<button onclick="alert(Date())">klik saya</button>\r\n<h3 onmouseenter="this.innerHTML=''Saya belajar JavaScript''">Arahkan pointer ke element ini</h3>', '', ''),
(20, 'BDMJggi', 'JavaScript Internal', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n  <title>Penulisan Script</title>\r\n</head>\r\n<body>\r\n  <h1>Internal JavaScript</h1>\r\n  \r\n  <h3>Apa yang kalian tahu tentang JavaScript?</h3>\r\n  <button id="btn">JAWAB</button>\r\n    \r\n<script>\r\n  var tombol = document.getElementById(''btn'');\r\n  tombol.onclick = function(){\r\n    alert("JavaScript adalah bahasa program untuk front-end website");\r\n  };\r\n</script>\r\n</body>\r\n</html>', '', ''),
(21, 'BDMJggi', 'JavaScript External', '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n  <title>Penulisan Script</title>\r\n</head>\r\n<body>\r\n  <h1>External JavaScript</h1>\r\n  \r\n  <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod\r\n  tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,\r\n  quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo\r\n  consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse\r\n  cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non\r\n  proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>\r\n  \r\n  <h3>Lihat, apa yang bisa dilakukan tombol ini</h3>\r\n  <button id="btn">klik saya</button>\r\n  \r\n  <script src="http://localhost/helloworld/assets/js-external.js"></script>\r\n\r\n</body>\r\n</html>', '', ''),
(22, 'ByTc2dL', 'Pop up Alert', '<!DOCTYPE html>\r\n<html>\r\n<body>\r\n  <h1>JavaScript Function Alert</h1>\r\n  \r\n  <button onclick="alert( 3 + 4 )">aler(number)</button>\r\n  <button onclick="alert( ''string javascript'' )">alert(string)</button>\r\n  <button onclick="alert( new Date() )">alert(function)</button>\r\n\r\n<p class="explain">\r\n  Pada setiap tombol telah disisipkan alert\r\n  yang akan mengeluarkan output berbeda ketika diklik,\r\n  yaitu output number, string dan juga global function Date()\r\n</p>\r\n</body>\r\n</html>', '', ''),
(23, 'ByTc2dL', 'Pop up Prompt', '<h1>JavaScript Function Prompt</h1>\r\n\r\n<button id="btn">prompt()</button>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var kotak = prompt(''ketik apapun di sini:'');\r\n  alert(''kamu mengetikkan : '' + kotak);\r\n};'),
(24, 'ByTc2dL', 'Pop up Confirm', '<h1>JavaScript Function Confirm</h1>\r\n\r\n<button id="btn">confirm()</button>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var tes = confirm(''Benar atau Salah?'');\r\n  alert(tes);\r\n};'),
(25, 'ByTc2dL', 'Confirm dan Pengkodisian', '<h1>Confirm dan Pengkodisian</h1>\r\n\r\n<button id="btn">confirm()</button>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var tes = confirm(''apakah yang akan kamu pilih?'');\r\n  if (tes === true) {\r\n    alert(''kamu memilih OK'');\r\n  } else {\r\n    alert(''kamu memilih CANCEL'');\r\n  }\r\n};'),
(26, 'ByTc2dL', 'innerHTML dan innerText', '<h1>innerHTML dan innerText</h1> \n\n<div id="text"></div>\n<button id="btn1">innerHTML</button>\n<button id="btn2">innerText</button>', 'div { \r\n  border: 1px solid red; padding: 5px; height: 100px; \r\n}', 'var tombol1 = document.getElementById(''btn1'');\nvar tombol2 = document.getElementById(''btn2'');\nvar div = document.getElementById(''text'');\n\ntombol1.onclick = function(){\n  div.innerHTML = ''<h3>Selamat belajar JavaScript</h3>'';\n};\n\ntombol2.onclick = function(){\n  div.innerText = ''<h3>Selamat belajar JavaScript</h3>'';\n};'),
(27, 'ByTc2dL', 'JavaScript Console', '<h1>JavaScript Console</h1>\r\n\r\n<button id="btn">console.log()</button>\r\n\r\n<p class="explain">\r\n  Buka Devtools (F12) tab console kemudian klik tombol di atas.\r\n  Console akan menampilkan output operasi penjumlahan dari 10 + 13.\r\n</p>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var angka = 10 + 13;\r\n  console.log(''hasil penjumlahannya adalah '' + angka);\r\n};'),
(28, 'ByTc2dL', 'JavaScript Console Debug', '<h1>JavaScript Console Debug</h1>\r\n\r\n<button id="btn">debug</button>\r\n\r\n<p class="explain">\r\n  Buka DevTools tab console kemudian klik tombol di atas.\r\n  Perhatikan penulisan variable angka pada console.log(),\r\n  yang dengan sengaja, penulisan variablenya tidak sesuai.\r\n  Program akan berhenti berjalan dengan menampilkan notifikasi pada console pada statement yang mengalami error.\r\n</p>\r\n</body>\r\n</html>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var angka = 10 + 13;\r\n  console.log(''hasil penjumlahannya adalah '' + bangka);\r\n};'),
(29, 'BykFmUK', 'Operator Penugasan', '<h1>Operator Assignment</h1>\r\n\r\n<h3>var num = 10, maka num += 5 adalah <span id="hasil">...</span></h3>\r\n<button id="btn">num += 5</button>\r\n\r\n<p class="explain">\r\n  variable num yang sebelumnya bernilai 10 ditimpa nilainya\r\n  dengan num itu sendiri yaitu 10 kemudian dijumlahkan dengan angka 5\r\n</p>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var num = 10;\r\n  document.getElementById(''hasil'').innerHTML = num += 5;\r\n};'),
(30, 'BykFmUK', 'Operator Perbandingan', '<h1>Operator Perbandingan</h1>\r\n\r\n<h3>Ketiga nilai ini (9, 10, dan "10") akan dibandingkan</h3>\r\n<button id="btn1">apakah 9 == 10</button>\r\n<button id="btn2">apakah 9 != 10</button>\r\n<button id="btn3">apakah 9 > 10</button>\r\n<button id="btn4">apakah 10 === "10"</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  // apakah 9 sama dengan 10\r\n  this.innerHTML = (9 == 10);\r\n  // alert(9 == 10);\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  // apakah 9 tidak sama dengan 10\r\n  this.innerHTML = (9 != 10);\r\n};\r\ndocument.getElementById(''btn3'').onclick = function(){\r\n  // apakah 9 lebih besar dari 10\r\n  this.innerHTML = (9 > 10);\r\n};\r\ndocument.getElementById(''btn4'').onclick = function(){\r\n  // apakah 10 sama nilai dan sama tipe dengan "10"\r\n  this.innerHTML = (10 === "10");\r\n};'),
(31, 'BykFmUK', 'Operator Logika AND', '<h1>Operator Logika AND</h1>\r\n\r\n<h3>Kita akan menguji angka itu termasuk ganjil atau genap</h3>\r\n<h3>DAN</h3>\r\n<h3>Kita akan memastikan angka itu lebih kecil dari 10</h3>\r\n<button id="btn">klik untuk mulai</button>\r\n\r\n<p class="explain">\r\n  Jika ingin menghasilkan nilai true dari logika program ini,\r\n  maka input yang harus dimasukkan adalah\r\n  angka genap yang berada pada rentang antara 0 sampai 10,\r\n  yaitu 0, 2, 4, 6, dan 8 saja.\r\n  Selain dari angka tersebut akan menghasilkan nilai false.\r\n</p>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var angka = prompt(''masukkan angka untuk diuji'');\r\n  // setiap angka jika dibagi dengan 2 pasti angka itu adalah genap\r\n  // jika dibagi 2 angka genap selalu menghasilkan sisa bagi 0\r\n  var expresi1 = angka % 2 === 0;\r\n  var expresi2 = angka < 10;\r\n  alert(expresi1 && expresi2);\r\n};'),
(32, 'BykFmUK', 'Operator Logika OR', '<h1>Operator Logika OR</h1>\r\n\r\n<h3>Kita akan menguji apakah angka itu termasuk ganjil atau genap</h3>\r\n<h3>ATAU</h3>\r\n<h3>Kita menguji apakah angka itu lebih kecil dari 10</h3>\r\n<button id="btn">klik untuk mulai</button>\r\n\r\n<p class="explain">\r\n  Agar menghasilkan nilai true, kemungkinan rentang angkanya adalah \r\n  selama angka itu genap tidak peduli angka itu lebih kecil atau lebih besar dari 10\r\n  dan juga selama angka itu lebih kecil dari 10 tidak peduli angka itu ganjil atau genap  \r\n</p>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var angka = prompt(''masukkan angka untuk diuji'');\r\n  var expresi1 = angka % 2 === 0;\r\n  var expresi2 = angka < 10;\r\n  alert(expresi1 || expresi2);\r\n};'),
(33, 'BykFmUK', 'Operator Negasi', '<h1>Operator Negasi</h1>\r\n\r\n<h3>Nilai dari expression 1 > 2 adalah false</h3>\r\n<h3>Jika dibalik dengan operator NEGASI maka hasilnya <span id="hasil">...</span></h3>\r\n<button id="btn">klik saya</button>', '', 'var tombol = document.getElementById(''btn'');\r\ntombol.onclick = function(){\r\n  var expression = 1 > 2;\r\n  document.getElementById(''hasil'').innerHTML = !expression;\r\n};'),
(34, 'BykFmUK', 'Operator String', '<h1>Operator String</h1>\r\n\r\n<button id="btn1">string + string</button>\r\n<button id="btn2">string + number + number</button>\r\n<button id="btn3">number + number + string</button>\r\n\r\n<p class="explain">\r\n  Penggunaan operator plus sebagai operator aritmatika atau sebagai operator string,\r\n  tergantung cara menempatkan urutan operand dari kiri ke kanan\r\n</p>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  alert(''belajar'' + '' javascript'');    \r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(''10'' + 10 + 10);\r\n};\r\ndocument.getElementById(''btn3'').onclick = function(){\r\n  alert(10 + 10 + ''10'');\r\n};'),
(35, 'BykFmUK', 'Operator Ternary', '<h1>Operator Ternary</h1>\r\n\r\n<h3>Masukkan angka untuk diuji</h3>\r\n<button id="btn">klik untuk mulai</button>', '', 'document.getElementById(''btn'').onclick = function(){\r\n  var num = prompt(''masukkan angka untuk diuji'');\r\n  var ternary = (num % 2 === 0) ? num + " angka genap" : num + " angka ganjil";\r\n  var check = (isNaN(num)) ? alert(num + '' bukan angka'') : alert(ternary);\r\n};'),
(36, 'BykFmUK', 'Operator Typeof', '<h1>Operator Typeof</h1>\r\n\r\n<button id="btn1">tipe data 10</button>\r\n<button id="btn2">tipe data "10"</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  alert(typeof 10);\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(typeof("10"));\r\n};'),
(37, 'BSi5Anl', 'Tipe Data Number', '<h1>Tipe Data Number</h1>\r\n\r\n<button id="btn1">number 23</button>\r\n<button id="btn2">number 3.14</button>\r\n<button id="btn3">number -13</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  alert(typeof 23);\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(typeof 3.14);\r\n};\r\ndocument.getElementById(''btn3'').onclick = function(){\r\n  alert(typeof -13);\r\n};'),
(38, 'BSi5Anl', 'Tipe Data String', '<h1>Tipe Data String</h1>\r\n\r\n<button id="btn1">"Hello World"</button>\r\n<button id="btn2">"1234"</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  alert(typeof "Hello World");\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(typeof "1234");\r\n};'),
(39, 'BSi5Anl', 'Tipe Data Boolean', '<h1>Tipe Data Boolean</h1>\r\n\r\n<button id="btn1">true</button>\r\n<button id="btn2">false</button>\r\n<button id="btn3">expression</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  alert(typeof true);\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(typeof false);\r\n};\r\ndocument.getElementById(''btn3'').onclick = function(){\r\n  var exp = 1 < 2;\r\n  alert(typeof exp);\r\n};'),
(40, 'BSi5Anl', 'Tipe Data Object', '<h1>Tipe Data Object dan Array</h1>\r\n\r\n<button id="btn1">obj = { nama: ''John'', umur: 23 }</button>\r\n<button id="btn2">arr = [''John'',23]</button>', '', 'document.getElementById(''btn1'').onclick = function(){\r\n  var obj = { nama: ''John'', umur: 23 };\r\n  alert(typeof obj);\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  var arr = [''John'',23];\r\n  alert(typeof arr);\r\n};'),
(41, 'BSi5Anl', 'Tipe Data Function', '<h1>Tipe Data Function</h1>\r\n\r\n<button id="btn1">jalankan function</button>\r\n<button id="btn2">cek tipe data</button>', '', 'function test(){\r\n  alert(''Hello World'');\r\n}\r\n\r\ndocument.getElementById(''btn1'').onclick = function(){\r\n  test();\r\n};\r\ndocument.getElementById(''btn2'').onclick = function(){\r\n  alert(typeof test);\r\n};'),
(42, 'BSi5Anl', 'Tipe Data Undefined', '<h1>Tipe Data Undefined</h1>\r\n\r\n<button id="btn">undefined</button>', '', 'document.getElementById(''btn'').onclick = function(){\r\n  var kosong;\r\n  alert(''nilai dari variable adalah '' + kosong);\r\n  alert(''tipe datanya adalah '' + typeof kosong);\r\n};'),
(43, 'BpMkfuO', 'Function Declaration', '<h1>Function Declaration</h1>\r\n\r\n<button id="btn">jumlah(10,13)</button>\r\n<!-- pemanggilan function lewat event handler -->\r\n<button onclick="jumlah(101,234)">jumlah(101,234)</button>\r\n<button onclick="jumlah(321,987)">jumlah(321,987)</button>', '', '// deklarasi function\r\nfunction jumlah(a,b){\r\n  alert(''hasilnya adalah '' + (a + b));\r\n}\r\n\r\ndocument.getElementById(''btn'').onclick = function(){\r\n  // pemanggilan function\r\n  jumlah(10,13);  \r\n};'),
(44, 'BpMkfuO', 'Function Expression', '<h1>Function Expression</h1>\r\n\r\n<button id="btn">jumlah(10,13)</button>', '', '// deklarasi function\r\nvar jumlah = function(a,b){\r\n  alert(''hasilnya adalah '' + (a + b));\r\n}\r\n\r\ndocument.getElementById(''btn'').onclick = function(){\r\n  // pemanggilan function expression\r\n  jumlah(10,13);\r\n};'),
(45, 'BpMkfuO', 'Anonymous Function', '<h1>Self Invoke Function</h1>\r\n\r\n<h3>Hasil penjumlahan 211 + 394 adalah <span id="hasil"></span></h3>\r\n\r\n<p class="explain">\r\nTerlihat seperti tidak ada perbedaan dengan menulis kode tanpa function.\r\nFunction dalam bentuk ini belum akan digunakan di dalam materi JavaScript Dasar.\r\nUntuk sekarang hanya perlu diperhatikan tata cara penulisannya\r\n</p>', '', '(function() {\r\n\r\n  document.getElementById(''hasil'').innerHTML = 211 + 394;\r\n\r\n})();'),
(46, 'B9PemM9', 'Parameter dan Argument', '<h1>Parameter dan Argument</h1>\r\n\r\n<button onclick="play()">function gabung kata</button>\r\n<h3 id="hasil"></h3>', '', '// deklarasi function\r\nfunction gabung(a,b) {\r\n  return a + " " + b;\r\n}\r\nfunction tampilkan(a) {\r\n  document.getElementById(''hasil'').innerHTML = a;\r\n}\r\n\r\nfunction play(){\r\n  var a = "function", b = "belajar", c = "javascript", d = "tentang";\r\n  var satu = gabung(b,c); // belajar javascript\r\n  var dua = gabung(d,a); // tentang function\r\n  var tiga = gabung(satu,dua); // Belajar JavaScript tentang function\r\n  \r\n  tampilkan(tiga); // tampilkan ke layar\r\n}'),
(47, 'B9PemM9', 'Parameter Conflict', '<h1>Parameter dan Argument</h1>\r\n\r\n<h3>Parameter lebih sedikit dari Argument</h3>\r\n<!-- pemanggilan function dengan argument nilai 1, 2, dan 3 -->\r\n<button onclick="jumlah(1,2,3)">jumlah(1,2,3)</button>\r\n\r\n<p class="explain">\r\nAngka 3 sebagai argument function akan diabaikan saat pemanggilan function\r\n</p>', '', '// deklarasi function dengan parameter a dan b\r\nfunction jumlah(a,b) {\r\n  var num = a + b;\r\n  alert(''hasilnya '' + num);\r\n}'),
(48, 'B9PemM9', 'Argument Conflict', '<h1>Parameter dan Argument</h1>\r\n\r\n<h3>Argument lebih sedikit dari Parameter</h3>\r\n<!-- pemanggilan function dengan argument nilai 1 dan 2 -->\r\n<button onclick="jumlah(1,2)">jumlah(1,2)</button>\r\n\r\n<p class="explain">\r\nNilai parameter c pada function akan diisi undefined\r\n</p>', '', '// deklarasi function dengan parameter a, b, dan c\r\nfunction jumlah(a,b,c) {\r\n  var num = a + b;\r\n  alert(''hasilnya '' + num)\r\n  alert(c);\r\n}'),
(49, 'B9PemM9', 'Arguments', '<h1>Arguments Function</h1>\r\n\r\n<button onclick="jumlah(1,2,3)">jumlah(1,2,3)</button>\r\n<button onclick="jumlah(1,2,3,4,5)">jumlah(1,2,3,4,5)</button>\r\n\r\n<p class="explain">\r\nDengan cara ini, memasukkan sebanyak apapun argument angka untuk dijumlahkan,\r\nmaka semua angkanya akan tetap dioperasikan dengan benar\r\n</p>', '', 'function jumlah() {\r\n  var hasil = 0;\r\n  var arg = arguments;\r\n  for( var i = 0; i < arg.length; i++){\r\n    hasil += arg[i];\r\n  }\r\n  alert(hasil);\r\n}'),
(50, 'Bsi6Z4d', 'Scope Variable JavaScript', '<h1>Scope Variable JavaScript</h1>\r\n\r\n<button onclick="play()">block scope</button>\r\n<button id="btn">function scope</button>', '', 'function play(){\r\n  var num = 10;\r\n  if (num % 2 === 0) {\r\n    var str;\r\n    str = num + " adalah angka genap";\r\n  } else {\r\n    str = num + " adalah angka ganjil";\r\n  }\r\n  // num dan str bisa digunakan di luar statement if\r\n  alert(str);\r\n}\r\n// num dan str tidak bisa digunakan di luar function play\r\ndocument.getElementById(''btn'').onclick = function(){\r\n  alert(str); // ReferenceError: str is not defined\r\n}'),
(51, 'Bsi6Z4d', 'Local Variable', '<h1>Local Variable</h1>\r\n\r\n<h3>Di dalam function, str adalah <u id="hasil1"></u></h3>\r\n<h3>Di luar function, str adalah <u id="hasil2"></u></h3>', '', 'function test() { \r\n  // deklarasi variable di dalam function\r\n  var str = ''variable lokal'';\r\n  document.getElementById(''hasil1'').innerHTML = str;\r\n}\r\ntest();\r\n// ReferenceError\r\ndocument.getElementById(''hasil2'').innerHTML = str;'),
(52, 'Bsi6Z4d', 'Global Variable', '<h1>Global Variable</h1>\r\n\r\n<h3>str adalah <u id="hasil1"></u></h3>\r\n<h3>Type data dari str adalah <u id="hasil2"></u></h3>', '', '// deklarasi variable di luar function\r\nvar str = ''variable global'';\r\n\r\nfunction test() { \r\n  // str juga bisa diakses di sini\r\n  str += '' yang bisa diakses dimana pun'';\r\n  document.getElementById(''hasil1'').innerHTML = str;\r\n}\r\ntest();\r\ndocument.getElementById(''hasil2'').innerHTML = typeof str;'),
(53, 'Bsi6Z4d', 'Auto Global Variable', '<h1>Auto Global Variable</h1>\r\n\r\n<h3>str adalah <u id="hasil1"></u></h3>\r\n<h3>Type data dari str adalah <u id="hasil2"></u></h3>', '', 'function test() { \r\n  // deklarasi variable tanpa var di dalam function\r\n  // str menjadi variable global\r\n  str = ''variable auto global'';\r\n}\r\ntest();\r\ndocument.getElementById(''hasil1'').innerHTML = str;\r\ndocument.getElementById(''hasil2'').innerHTML = typeof str;'),
(54, 'Bsi6Z4d', 'Name Conflict Variable', '<h1>Name Conflict Variable</h1>\r\n\r\n<button onclick="play1()">local</button>\r\n<button onclick="play2()">global</button>', '', 'var str = ''variable global di luar function'';\r\n\r\nfunction play1() {\r\n  var str = ''variable lokal di dalam function'';\r\n  alert(str); // str akan mengacu pada deklarasi lokal\r\n}\r\n\r\nfunction play2(){\r\n  alert(str); // str akan mengacu pada deklarasi global\r\n}'),
(55, 'Bsi6Z4d', 'Name Conflict Parameter', '<h1>Name Conflict Parameter</h1>\r\n\r\n<button onclick="play()">Variable Global</button>\r\n<!-- argument string -->\r\n<button onclick="test(''Hello World'')">Variable Parameter</button>', '', 'var str = ''global variable''; \r\n\r\nfunction test(str) { // str akan mengacu pada argument\r\n  alert(str); // str akan mengacu pada parameter\r\n}\r\nfunction play(){\r\n  alert(str); // str akan mengacu pada variable global\r\n}'),
(56, 'BP9LPmJ', 'Penulisan String', '<h1>Penulisan String</h1>\r\n\r\n<button onclick="play1()">kutip double</button>\r\n<button onclick="play2()">kutip single</button>', '', 'function play1(){\r\n  // string yang terdapat tanda kutip tunggal\r\n  var str = "Apakah sekarang adalah hari Jum''at ?";\r\n  alert(str);\r\n}\r\nfunction play2(){\r\n  // string yang terdapat tanda kutip ganda\r\n  var str = ''Saya sedang belajar "JavaScript" di sebuah website'';\r\n  alert(str);  \r\n}'),
(57, 'BP9LPmJ', 'Penggabungan String', '<h1>Penggabungan String</h1>\r\n\r\n<button onclick="play()">concat</button>\r\n<h3 id="hasil"></h3>', '', 'function play(){\r\n  var str1 = ''Pada hari Jum\\''at kemarin, jam'',\r\n  str2 = '' 12'',\r\n  str3 = '' siang, saya belajar "JavaScript".'',\r\n  newStr = str1 + str2 + str3;\r\n  document.getElementById(''hasil'').innerHTML = newStr;\r\n}'),
(58, 'BP9LPmJ', 'String Literal', '<h1>String Literal</h1>\r\n\r\n<button onclick="play()">klik</button> ', '', 'function play(){\r\n  var str1 = `Jum''at kemarin, saya belajar "JavaScript" di sebuah website`;\r\n  var str2 = `Selamat datang\r\n  di website kami,\r\n    selamat belajar`;\r\n  alert(str1);\r\n  alert(str2);\r\n}'),
(59, 'BP9LPmJ', 'Property Length String', '<h1>Property Length String</h1>\r\n\r\n<button onclick="play()">string.length</button> ', '', 'function play(){\r\n  var str = "1 234";\r\n  alert(str.length);\r\n}'),
(60, 'BOddR86', 'Method indexOf String', '<h1>Method indexOf String</h1>\r\n\r\n<h3>string "Hello World"</h3>\r\n<button onclick="play1()">string.indexOf("e")</button>\r\n<button onclick="play2()">string.indexOf("i")</button>', '', 'var str = "Hello World";\r\nfunction play1(){\r\n  alert(str.indexOf("l")); // 2\r\n}\r\nfunction play2(){\r\n  alert(str.indexOf("h")); // -1\r\n}'),
(61, 'BOddR86', 'Method charAt String', '<h1>Method charAt String</h1>\r\n\r\n<h3>string "Hello World"</h3>\r\n<button onclick="play()">string.charAt(6)</button>', '', 'function play(){\r\n  var str = "Hello World";\r\n  alert(str.charAt(6));\r\n}'),
(62, 'BOddR86', 'Method Match String', '<h1>Method Match String</h1>\r\n\r\n<button onclick="play()">string.match(CSS)</button>', '', 'var str = `JavaScript merupakan bahasa pemrograman murni, \r\n  tidak seperti HTML atau CSS. JavaScript memiliki tingkat kompleksitas \r\n  yang lebih tinggi dibandingkan dengan html atau css.`;\r\nfunction play(){\r\n  // flag i untuk mencari tanpa mempedulikan caseSensitif\r\n  // flag g untuk mencari semua kecocokan yang ditemukan\r\n  alert(str.match(/css/ig));\r\n}'),
(63, 'BOddR86', 'Method replace String', '<h1>Method replace String</h1>\r\n\r\n<p id="text">\r\n  HTML adalah bahasa pemrograman tingkat tinggi dan dinamis.\r\n  HTML populer di internet dan dapat bekerja di sebagian besar web browser \r\n  seperti Chrome, IE, Firefox, dan Opera.\r\n  Kode HTML dapat disisipkan dalam halaman web menggunakan tag script\r\n</p>\r\n<button onclick="play()">string.replace("HTML","JavaScript")</button>', '', 'var text = document.getElementById(''text'').innerHTML;\r\nfunction play(){\r\n  var hasil = text.replace(/HTML/ig, ''<u>JavaScript</u>'');\r\n  document.getElementById(''text'').innerHTML = hasil;\r\n}'),
(64, 'BOddR86', 'Method slice String', '<h1>Method slice String</h1>\r\n\r\n<h3>string "Hello World Whoooa"</h3>\r\n<button onclick="play()">string.slice(6,11)</button>', '', 'function play(){\r\n  var str = "Hello World Whoooa";\r\n  alert( str.slice(6,11) );\r\n}'),
(65, 'BOddR86', 'Method substr String', '<h1>Method substr String</h1>\r\n\r\n<h3>string "Hello World Whoooa"</h3>\r\n<button onclick="play()">string.substr()</button>', '', 'function play(){\r\n  var text = "Hello World Whooa";\r\n  // potong dari index ke-6 sampai 8 karakter\r\n  alert( text.substr(6, 8) );\r\n}'),
(66, 'BOddR86', 'Method split String', '<h1>Method split String</h1>\r\n\r\n<button onclick="play()">string.split('' '')</button>', '', 'function play(){\r\n  var str = "JavaScript adalah bahasa program yang dinamis";\r\n  // pisahkan string berdasarkan spasi menjadi potongan array\r\n  var hasil = str.split('' '');\r\n  alert(hasil);\r\n  alert(typeof hasil); // object array\r\n}'),
(67, 'BOddR86', 'Method toString String', '<h1>Method toString String</h1>\r\n\r\n<button onclick="play1()">number => toString()</button>\r\n<button onclick="play2()">boolean => toString()</button>', '', 'function play1(){\r\n  var num = 10; // number\r\n  var numStr = num.toString();\r\n  alert(numStr);\r\n  alert(typeof numStr); // string\r\n}\r\nfunction play2(){\r\n  var bool = true; // boolean\r\n  var boolStr = bool.toString();\r\n  alert(boolStr);\r\n  alert(typeof boolStr); // string\r\n}'),
(68, 'BOddR86', 'Method toLowerCase String', '<h1>Method toLowerCase String</h1>\r\n\r\n<button onclick="play()">string.toLowerCase()</button>', '', 'function play(){\r\n  var str = "Hello World";\r\n  alert( str.toLowerCase() );\r\n}'),
(69, 'BOddR86', 'Method toUpperCase String', '<h1>Method toUpperCase String</h1>\r\n\r\n<h3>Konversi string "Hello World" menjadi huruf besar</h3>\r\n<button onclick="play()">string.toUpperCase()</button>', '', 'function play(){\r\n  var str = "Hello World";\r\n  alert( str.toUpperCase() );\r\n}'),
(70, 'BOddR86', 'Method trim String', '<h1>Method trim String</h1>\r\n\r\n<pre><span id="text">  Java Script     </span></pre>\r\n<button onclick="play()">string.trim()</button>', 'pre {\r\n  background: lightblue;\r\n}\r\nspan {\r\n  background: yellow;\r\n}', 'function play(){\r\n  var str = document.getElementById(''text'').innerHTML;\r\n  var hasil = str.trim();\r\n  document.getElementById(''text'').innerHTML = hasil;\r\n}'),
(71, 'B2cqdXh', 'Integer Number', '<h1>Integer Number</h1>\r\n\r\n<button onclick="play1()">Integer 15 digit</button>\r\n<button onclick="play2()">Integer 16 digit</button>', '', 'function play1(){\r\n  alert(999999999999999); // 15 digit\r\n  // 999999999999999\r\n}\r\nfunction play2(){\r\n  alert(9999999999999999); // 16 digit\r\n  // 10000000000000000\r\n}'),
(72, 'B2cqdXh', 'Float Number', '<h1>Float Number</h1>\r\n\r\n<button onclick="play1()">0.3 + 0.7</button>\r\n<button onclick="play2()">0.1 + 0.2 </button>\r\n<button onclick="play3()">0.1 + 0.2 == 0.3</button>', '', 'function play1(){\r\n  alert(0.3 + 0.7); // 1\r\n}\r\nfunction play2(){\r\n  alert(0.1 + 0.2); // 0.30000000000000004\r\n}\r\nfunction play3(){\r\n  alert((0.2 * 10 + 0.1 * 10) / 10); // 0.3\r\n}'),
(73, 'B2cqdXh', 'Exponent Notation', '<h1>Exponent Notation</h1>\r\n\r\n<button onclick="play1()">Exponent 1e6</button>\r\n<button onclick="play2()">Exponent 1e-6</button>', '', 'function play1(){\r\n  alert(1e6); // 1000000\r\n}\r\nfunction play2(){\r\n  alert(1e-6); // 0.000001\r\n}'),
(74, 'B2cqdXh', 'Negative Number', '<h1>Negative Number</h1>\r\n\r\n<button onclick="play1()">- 3 + 7</button>', '', 'function play1(){\r\n  alert(-3 + 7); // 4\r\n}'),
(75, 'B2cqdXh', 'Infinity Number', '<h1>Number Infinity dan -Infinity</h1>\r\n\r\n<button onclick="play1()">2 / 0</button>\r\n<button onclick="play2()">-2 / 0</button>', '', 'function play1(){\r\n  alert(2 / 0); // Infinity\r\n}\r\nfunction play2(){ // -Infinity\r\n  alert(-2 / 0);\r\n}'),
(76, 'B2cqdXh', 'Number NaN', '<h1>Number NaN</h1>\r\n\r\n<button onclick="play1()">10 + "string" = ?</button>\r\n<button onclick="play2()">0 / 0 = ?</button>', '', 'function play1(){\r\n  var num = 10 / "text"; // number dibagi string\r\n  alert(''hasilnya adalah ''+ num);\r\n  alert(''tipe datanya adalah '' + typeof num);\r\n}\r\nfunction play2(){\r\n  var num = 0 / 0; // angka 0 dibagi 0\r\n  alert(''hasilnya adalah '' + num);\r\n  alert(''tipe datanya adalah '' + typeof num);\r\n}'),
(77, 'B2cqdXh', 'Global Function isNaN', '<h1>Global Function isNaN</h1>\r\n\r\n<button onclick="play1()">"123" === NaN</button>\r\n<button onclick="play2()">100 / "string" === NaN</button>', '', 'function play1(){\r\n  var str = "1234";\r\n  alert(isNaN(str)); // false\r\n}\r\nfunction play2(){\r\n  var str = 100 / "string";\r\n  alert(isNaN(str)); // true\r\n}'),
(78, 'BKW2Ki0', 'Method toFixed Number', '<h1>Method toFixed Number</h1>\r\n\r\n<button onclick="play()">number.toFixed()</button>', '', 'function play(){\r\n  var num = 12.344;\r\n  alert( num.toFixed(0) ); // tanpa koma\r\n  alert( num.toFixed(1) ); // satu angka di belakang koma\r\n  alert( num.toFixed(2) ); // dua angka di belakang koma\r\n  alert( num.toFixed(3) ); // tiga angka di belakang koma\r\n  alert( num.toFixed(4) ); // empat angka di belakang koma\r\n}'),
(79, 'BKW2Ki0', 'Method toExponential Number', '<h1>Method toExponential Number</h1>\r\n\r\n<button onclick="play()">number.toExponential()</button>', '', 'function play(){\r\n  var num = 9.656;\r\n  alert( num.toExponential(2) );\r\n  alert( num.toExponential(4) );\r\n  alert( num.toExponential(6) );\r\n}'),
(80, 'BKW2Ki0', 'Method toPrecision Number', '<h1>Method toPrecision Number</h1>\r\n\r\n<button onclick="play()">number.toPrecision()</button>', '', 'function play(){\r\n  var num = 12.344;\r\n  alert( num.toPrecision(1) );\r\n  alert( num.toPrecision(2) );\r\n  alert( num.toPrecision(3) );\r\n}'),
(81, 'BKW2Ki0', 'Global Function Number', '<h1>Global Function Number</h1>\r\n\r\n<button onclick="play1()">Number(true)</button>\r\n<button onclick="play2()">Number( new Date() )</button>\r\n<button onclick="play3()">Number("100")</button>\r\n<button onclick="play4()">Number("10 20")</button>', '', 'var bool = true, date = new Date(), str1 = "100", str2 = "10 20";\r\nfunction play1() {\r\n  alert( Number(bool) ); // boolean\r\n}\r\nfunction play2() {\r\n  alert( Number(date) ); // waktu saat ini dalam milidetik\r\n}\r\nfunction play3() {\r\n  alert( Number(str1) ); // 100\r\n}\r\nfunction play4() {\r\n  alert( Number(str2) ); // NaN\r\n}'),
(82, 'BKW2Ki0', 'Global Function parseInt', '<h1>Global Function parseInt</h1>\r\n\r\n<button onclick="play()">parseInt()</button>', '', 'function play(){\r\n  alert( parseInt("10") ); // 10\r\n  alert( parseInt("12.33") ); // 12\r\n  alert( parseInt("13 23 33") ); // 13\r\n  alert( parseInt("11 years") ); // 11\r\n  alert( parseInt("years 12") ); // NaN\r\n}'),
(83, 'BKW2Ki0', 'Global Function parseFloat', '<h1>Global Function parseFloat</h1>\r\n\r\n<button onclick="play()">parseFloat()</button>', '', 'function play(){\r\n  alert( parseFloat("10") ); // 10\r\n  alert( parseFloat("12.33") ); // 12.33\r\n  alert( parseFloat("13 23 33") ); // 13\r\n  alert( parseFloat("11 years") ); // 11\r\n  alert( parseFloat("years 12") ); // NaN\r\n}'),
(84, 'BKW2Ki0', 'Method Math random', '<h1>Method Math Random</h1>\r\n\r\n<button onclick="play()">Math.random()</button>\r\n<h3 id="hasil"></h3>', '', 'function play(){\r\n  var rand = Math.random();\r\n  document.getElementById(''hasil'').innerHTML = rand;\r\n}'),
(85, 'BKW2Ki0', 'Method Math min', '<h1>Method Math Min</h1>\r\n\r\n<button onclick="play()">Math.min()</button>\r\n<h3 id="hasil"></h3>', '', 'function play(){\r\n  var num = Math.min(0, 150, 30, 20, -8, -200);\r\n  document.getElementById(''hasil'').innerHTML = num;\r\n}'),
(86, 'BKW2Ki0', 'Method Math max', '<h1>Method Math Max</h1>\r\n\r\n<button onclick="play()">Math.max()</button>\r\n<h3 id="hasil"></h3>', '', 'function play(){\r\n  var num = Math.max(0, 150, 30, 20, -8, -200);\r\n  document.getElementById(''hasil'').innerHTML = num;\r\n}'),
(87, 'BKW2Ki0', 'Method Math round', '<h1>Method Math round</h1>\r\n\r\n<button onclick="play1()">Math.round(3.14)</button>\r\n<button onclick="play2()">Math.round(3.789)</button>', '', 'function play1(){\r\n  alert( Math.round(3.14) ); // 3\r\n}\r\nfunction play2(){\r\n  alert( Math.round(3.789) ); // 4\r\n}'),
(88, 'BKW2Ki0', 'Method Math ceil', '<h1>Method Math ceil</h1>\r\n\r\n<button onclick="play1()">Math.ceil(3.14)</button>\r\n<button onclick="play2()">Math.ceil(3.789)</button>', '', 'function play1(){\r\n  alert( Math.ceil(3.14) ); // 4\r\n}\r\nfunction play2(){\r\n  alert( Math.ceil(3.789) ); // 4\r\n}'),
(89, 'BKW2Ki0', 'Method Math floor', '<h1>Method Math floor</h1>\r\n\r\n<button onclick="play1()">Math.floor(3.14)</button>\r\n<button onclick="play2()">Math.floor(3.789)</button>', '', 'function play1(){\r\n  alert( Math.floor(3.14) ); // 3\r\n}\r\nfunction play2(){\r\n  alert( Math.floor(3.789) ); // 3\r\n}'),
(90, 'BKW2Ki0', 'Math Custom random', '<h1>Math Custom random</h1>\r\n\r\n<h3>Cetak angka random min = 1, max = 9</h3>\r\n<button onclick="play()">custom random</button>\r\n<h3 id="hasil"></h3>', '', 'function play(){\r\n  var range = 8; // rentang\r\n  var min = 1; // batas minimal\r\n  // batas maksimal didapat dari range + min\r\n  \r\n  var rand = Math.random() * range + min; // trik\r\n  var int = Math.round(rand); // dibulatkan\r\n  document.getElementById(''hasil'').innerHTML += int + '' '';\r\n}'),
(91, 'BOG5lsN', 'Property Array length', '<h1>Property Array length</h1>\r\n\r\n<button onclick="play1()">array.length</button>\r\n<button onclick="play2()">array[6]</button>', '', 'var hari = ["senin","selasa","rabu","kamis","jum\\''at","sabtu","minggu"];\r\nfunction play1(){\r\n  alert(hari.length); // 7\r\n}\r\nfunction play2(){\r\n  alert(hari[6]); // minggu\r\n}'),
(92, 'BOG5lsN', 'Multiple Data Type Array', '<h1>Multiple Data Type Array</h1>\r\n\r\n<button onclick="play1()">array[0]</button>\r\n<button onclick="play2()">array[1]</button>\r\n<button onclick="play3()">array[2]</button>\r\n<button onclick="play4()">array[3]</button>', '', 'var arr = ["John",25,true,play1];\r\nfunction play1(){\r\n  alert(typeof arr[0]); // string\r\n}\r\nfunction play2(){\r\n  alert(typeof arr[1]); // number\r\n}\r\nfunction play3(){\r\n  alert(typeof arr[2]); // boolean\r\n}\r\nfunction play4(){\r\n  alert(typeof arr[3]); // function\r\n}'),
(93, 'BOG5lsN', 'Array is Object', '<h1>Array is Object</h1>\r\n\r\n<button onclick="play()">typeof arr</button>', '', 'function play(){\r\n  var arr = [1,2,3,4];\r\n  alert(typeof arr);\r\n}'),
(94, 'BOG5lsN', 'Multidimensional Array', '<h1>Multidimensional Array</h1>\r\n\r\n<button onclick="play1()">arr[3]</button>\r\n<button onclick="play2()">arr[3][1]</button>', '', 'var arr = ["John",25,true,["tinggi","gemuk","putih"]];\r\nfunction play1(){\r\n  alert(arr[3]); // array\r\n}\r\nfunction play2(){\r\n  alert(arr[3][2]); // putih\r\n}'),
(95, 'BAOmBpg', 'Method toString Array', '<h1>Method toString Array</h1>\r\n\r\n<button onclick="play()">arr.toString()</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  var str = arr.toString();\r\n  alert(typeof str);\r\n}'),
(96, 'BAOmBpg', 'Method join Array', '<h1>Method join Array</h1>\r\n\r\n<button onclick="play()">arr.join('' | '')</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  var str = arr.join('' | '');\r\n  alert(str);\r\n}'),
(97, 'BAOmBpg', 'Method pop Array', '<h1>Method pop Array</h1>\r\n\r\n<button onclick="play()">arr.pop()</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  alert( arr.pop() ); // kucing\r\n  alert(arr); // ["kelinci","koala"]\r\n}'),
(98, 'BAOmBpg', 'Method push Array', '<h1>Method push Array</h1>\r\n\r\n<button onclick="play()">arr.push()</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala"];\r\n  alert(arr); // ["kelinci","koala"]\r\n  arr.push("gajah");\r\n  alert(arr); // ["kelinci","koala","gajah"]\r\n  arr.push("harimau","kuda");\r\n  alert(arr); // ["kelinci","koala","gajah","harimau","kuda"]\r\n}'),
(99, 'BAOmBpg', 'Method shift Array', '<h1>Method shift Array</h1>\r\n\r\n<button onclick="play()">arr.shift()</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  alert( arr.shift() ); // kelinci\r\n  alert(arr); // ["koala","kucing"]\r\n}'),
(100, 'BAOmBpg', 'Method unshift Array', '<h1>Method unshift Array</h1>\r\n\r\n<button onclick="play()">arr.unshift()</button>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  alert( arr.unshift("gajah") ); // length 4\r\n  alert(arr); // ["gajah","kelinci","koala","kucing"]\r\n}'),
(101, 'BAOmBpg', 'Method splice Array', '<h1>Method splice Array</h1>\r\n\r\n<button onclick="play()">arr.splice(2,0,"gajah")</button>', '', '​\r\nfunction play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  // 2 = index posisi dimulainya eksekusi\r\n  // 0 = jumlah element yang dihapus\r\n  // "gajah" = elemen baru yang dimasukkan\r\n  arr.splice(2,0,"gajah");\r\n  alert(arr); // ["kelinci","koala","gajah","kucing"]\r\n}'),
(102, 'BAOmBpg', 'Method slice Array', '<h1>Method slice Array</h1>\r\n\r\n<button onclick="play()">array.slice(1,3)</button>', '', 'function play(){\r\n  var arr1 = ["kelinci","koala","kucing","gajah"];\r\n  // 1 = index dimulainya pemotongan\r\n  // 3 : index posisi akhir pemotongan\r\n  var arr2 = arr1.slice(1,3);\r\n  alert(arr2); // ["koala","kucing"]\r\n}'),
(103, 'BAOmBpg', 'Method sort Array', '<h1>Method sort Array</h1>\r\n\r\n<button onclick="play1()">array.sort(string)</button>\r\n<button onclick="play2()">array.sort(number)</button>', '', 'function play1(){\r\n  var arr1 = ["elang","beruang","ayam","cicak","domba"];\r\n  var arr2 = arr1.sort();\r\n  alert(arr2);\r\n}\r\nfunction play2(){\r\n  var arr1 = [3,8,2,9,1];\r\n  var arr2 = arr1.sort();\r\n  alert(arr2);\r\n}'),
(104, 'BAOmBpg', 'Method concat Array', '<h1>Method concat Array</h1>\r\n\r\n<button onclick="play()">array.concat()</button>', '', 'function play(){\r\n  var arr1 = ["satu","dua","tiga"];\r\n  var arr2 = ["empat","lima","enam"];\r\n  alert( arr1.concat(arr2) );\r\n}'),
(105, 'BAOmBpg', 'Method reverse Array', '<h1>Method reverse Array</h1>\r\n\r\n<button onclick="play()">array.reverse()</button>', '', 'function play(){\r\n  var arr = ["satu","dua","tiga","empat","lima","enam"];\r\n  alert( arr.reverse() );\r\n}'),
(106, 'BulG4tB', 'Method forEach Array', '<h1>Method forEach Array</h1>\r\n\r\n<h3>Cetak ke layar semua yang ada dalam array</h3>\r\n<button onclick="play()">array.forEach()</button>\r\n<div id="hasil"></div>', '', 'function play(){\r\n  var arr = ["kelinci","koala","kucing"];\r\n  var cetak = '''';\r\n  arr.forEach(function(element,index,array){\r\n    cetak += ''array ke ''+ index + '' adalah '' + element + '' dari array ''+ array +''<br>'';\r\n  });\r\n  document.getElementById(''hasil'').innerHTML = cetak;\r\n}'),
(107, 'BulG4tB', 'Method map Array', '<h1>Method map Array</h1>\r\n\r\n<h3>array = ["kelinci","koala","kucing"]</h3>\r\n<h3>Ubah semua element array menjadi huruf besar</h3>\r\n<button onclick="play()">array.map()</button>\r\n<div id="hasil"></div>', '', 'function play(){\r\n  var arr1 = ["kelinci","koala","kucing"];\r\n  var arr2 = [];\r\n  arr2 = arr1.map(function(element,index,array){\r\n    return element.toUpperCase();\r\n  });\r\n  document.getElementById(''hasil'').innerHTML = arr2.join('' - '');\r\n}'),
(108, 'BulG4tB', 'Method every Array', '<h1>Method every Array</h1>\r\n\r\n<h3>array = [42,28,20,73,58]</h3>\r\n<h3>apakah semua element lebih besar dari 10 ?</h3>\r\n<button onclick="play()">array.every()</button>', '', 'function play(){\r\n  var arr = [42,28,20,73,58];\r\n  var bool = arr.every(function(element,index,array){\r\n    return element > 10;\r\n  });\r\n  alert(bool);\r\n}'),
(109, 'BulG4tB', 'Method some Array', '<h1>Method some Array</h1>\r\n\r\n<h3>array = [42,28,2,73,58]</h3>\r\n<h3>apakah ada element yang lebih kecil dari 10</h3>\r\n<button onclick="play()">array.some()</button>', '', 'function play(){\r\n  var arr1 = [42,28,1,73,58];\r\n  var bool = arr1.some(function(element,index,array){\r\n    return element < 10;\r\n  });\r\n  alert(bool);\r\n}'),
(110, 'BulG4tB', 'Method filter Array', '<h1>Method filter Array</h1>\r\n\r\n<h3>array = [4,2,1,7,5,9,20,15,13]</h3>\r\n<h3>Cari angka yang lebih kecil dari 10</h3>\r\n<button onclick="play()">array.filter()</button>', '', 'function play(){\r\n  var arr1 = [4,2,1,7,5,9,20,15,13];\r\n  var arr2 = arr1.filter(function(element,index,array){\r\n    return element < 10;\r\n  });\r\n  alert( arr2.join('' - '') );\r\n}'),
(111, 'BulG4tB', 'Method find Array', '<h1>Method find Array</h1>\r\n\r\n<h3>array = [18,2,8,7,5,9,20,15,13]</h3>\r\n<h3>Cari angka yang lebih kecil dari 10</h3>\r\n<button onclick="play()">array.find()</button>', '', 'function play(){\r\n  var arr1 = [18,2,8,7,5,9,20,15,13];\r\n  var arr2 = arr1.find(function(element,index,array){\r\n    return element < 10;\r\n  });\r\n  alert(arr2);\r\n}');
INSERT INTO `snippet` (`id`, `relation`, `title`, `htm`, `css`, `jsc`) VALUES
(112, 'BulG4tB', 'Method includes Array', '<h1>Method includes Array</h1>\r\n\r\n<h3>array = ["koala","kucing","domba"]</h3>\r\n<button onclick="play1()">array.includes(''kucing'')</button>\r\n<button onclick="play2()">array.includes(''gajah'')</button>', '', 'var arr = ["koala","kucing","domba"];\r\nfunction play1(){\r\n  alert( arr.includes(''kucing'') );\r\n}\r\nfunction play2(){\r\n  alert( arr.includes(''gajah'') );\r\n}');

